jQuery(document).ready(function(){
    var publishers = jQuery("[data-pub]");
    publishers.attr('id', function(i, id){
        if(typeof(id) == "undefined" || id == "" || !id){
            return i+Math.floor(Math.random()*11);    
        } 
    });
    publishers.on("click", "body", function(e){
        e.preventDefault();
        var publisher = jQuery(this);
        var pubtype = publisher.data("pub");

        alert("pubtype"+pubtype);
        
        var url1 = publisher.attr("href").split("?");
        var action = url1[0].split("action/")[1];
        var params = jQuery.deparam(url1[1]);
        jQuery.each(params, function(key, value){
            if(key.indexOf("__elgg") != -1){
                delete params[key];
            }       
        });

        var actors = [];
        var actor1 = params;
        actor1.action = action;
        actor1.type = "action";
        actors.push(actor1);

        alert("actors"+jQuery.toJSON(actors));
                
        var subscribers = jQuery("[data-sub="+pubtype+"]");
        subscribers.each(function(index){
            var temp = {};
            var subscriber = jQuery(this);
            if(subscriber.data("refresh")){
                temp = subscriber.data();                
                temp.target = subscriber.attr("id");
            }else{
                var viewmaster = subscriber.parents("[data-view]")[0];
                temp = viewmaster.data();
                temp.target = viewmaster.attr("id");
            }
            
            temp.type = "refresh";
            actors.push(temp);            
        });
        
        alert("actors"+jQuery.toJSON(actors));
/*        elgg.action("parseAJAX",{
            data: jQuery.toJSON(actors),
            success: function(data) {
                alert(data);
                var returnArr = jQuery.parseJSON(data);                
            }  
        });  */              
    });
});

